mapper.select:
(
    SELECT *
    FROM questions
    WHERE "courseId" = #{courseId} AND tag = '选择'
      AND "kgPoint" = #{kgPoint}
    ORDER BY RAND()
    LIMIT #{selectCount}
)
UNION ALL
(
    SELECT *
    FROM questions
    WHERE "courseId" = #{courseId} AND tag = '简答'
    ORDER BY RAND()
    LIMIT #{shortAnswerCount}
)

SELECT id, title, description, "courseId", "selectA", "selectB", "selectC", "selectD", answer, "kgPoint", tag, score
FROM questions
WHERE id = #{id}
